package com.felixgrund.codestory.ast.parser.impl;import com.felixgrund.codestory.ast.parser.Yfunction;import com.felixgrund.codestory.ast.entities.Yparameter;import com.felixgrund.codestory.ast.exceptions.ParseException;import com.felixgrund.codestory.ast.parser.AbstractParser;import com.felixgrund.codestory.ast.parser.Yparser;import jdk.nashorn.internal.ir.FunctionNode;import jdk.nashorn.internal.ir.visitor.SimpleNodeVisitor;import jdk.nashorn.internal.parser.Parser;import jdk.nashorn.internal.runtime.Context;import jdk.nashorn.internal.runtime.ErrorManager;import jdk.nashorn.internal.runtime.Source;import jdk.nashorn.internal.runtime.options.Options;import java.util.ArrayList;import java.util.List;public class JsParser extends AbstractParser implements Yparser {	private Options parserOptions;	private FunctionNode rootFunctionNode;	public JsParser(String fileName, String fileContent) {		super(fileName, fileContent);		this.parserOptions = new Options("nashorn");		this.parserOptions.set("anon.functions", true);		this.parserOptions.set("parse.only", true);		this.parserOptions.set("scripting", true);		this.parserOptions.set("language", "es6");	}	@Override	public Object parse() throws ParseException {		ClassLoader classLoader = Thread.currentThread().getContextClassLoader();		ErrorManager errorManager = new ErrorManager();		Context context = new Context(this.parserOptions, errorManager, classLoader);		Source source = Source.sourceFor(this.fileName, this.fileContent);		Parser originalParser = new Parser(context.getEnv(), source, errorManager);		this.rootFunctionNode = originalParser.parse();		if (this.rootFunctionNode == null) {			throw new ParseException("Could not parse root function node", this.fileName, this.fileContent);		}		return this.rootFunctionNode;	}	@Override	public boolean functionNamesConsideredEqual(String aName, String bName) {		return aName != null && bName != null &&				(aName.equals(bName) || (aName.startsWith("L:") && bName.startsWith("L:")));	}	@Override	public Yfunction findFunctionByNameAndLine(String name, int line) {		Yfunction ret = null;		FunctionNode node = findFunction(new FunctionNodeVisitor() {			@Override			public boolean nodeMatches(FunctionNode functionNode) {				return functionNode.getLineNumber() == line && functionNode.getIdent().getName().equals(name);			}		});		if (node != null) {			ret = new JsFunction(node);		}		return ret;	}	@Override	public List<Yfunction> findFunctionsByLineRange(int beginLine, int endLine) {		List<FunctionNode> matchedFunctions = findAllFunctions(new FunctionNodeVisitor() {			@Override			public boolean nodeMatches(FunctionNode functionNode) {				int lineNumber = functionNode.getLineNumber();				return lineNumber >= beginLine && lineNumber <= endLine;			}		});		return transformNodes(matchedFunctions);	}	@Override	public List<Yfunction> getAllFunctions() {		List<FunctionNode> nodes = findAllFunctions(new FunctionNodeVisitor() {			@Override			public boolean nodeMatches(FunctionNode functionNode) {				return !":program".equals(functionNode.getName());			}		});		return transformNodes(nodes);	}	@Override	public List<Yfunction> findFunctionsByOtherFunction(Yfunction otherFunction) {		String functionNameOther = otherFunction.getName();		List<Yparameter> parametersOther = otherFunction.getParameters();		List<FunctionNode> matchedFunctions = findAllFunctions(new FunctionNodeVisitor() {			@Override			public boolean nodeMatches(FunctionNode functionNode) {				Yfunction yfunction = new JsFunction(functionNode);				List<Yparameter> parametersThis = yfunction.getParameters();				String functionNameThis = yfunction.getName();				boolean nameMatches = functionNamesConsideredEqual(functionNameOther, functionNameThis);				boolean paramsMatch = parametersThis.equals(parametersOther);				return nameMatches && paramsMatch;			}		});		return transformNodes(matchedFunctions);	}	private List<Yfunction> transformNodes(List<FunctionNode> nodes) {		List<Yfunction> functions = new ArrayList<>();		for (FunctionNode node : nodes) {			functions.add(new JsFunction(node));		}		return functions;	}	private FunctionNode findFunction(FunctionNodeVisitor visitor) {		FunctionNode ret = null;		this.rootFunctionNode.accept(visitor);		List<FunctionNode> matchedNodes = visitor.getMatchedNodes();		if (matchedNodes.size() > 0) {			ret = matchedNodes.get(0);		}		return ret;	}	private List<FunctionNode> findAllFunctions(FunctionNodeVisitor visitor) {		this.rootFunctionNode.accept(visitor);		return visitor.getMatchedNodes();	}	public static abstract class FunctionNodeVisitor extends SimpleNodeVisitor {		private List<FunctionNode> matchedNodes = new ArrayList<>();		public abstract boolean nodeMatches(FunctionNode functionNode);		@Override		public boolean enterFunctionNode(FunctionNode functionNode) {			if (nodeMatches(functionNode)) {				matchedNodes.add(functionNode);			}			return true;		}		public List<FunctionNode> getMatchedNodes() {			return matchedNodes;		}	}}